import React, { useState } from 'react'
import Joi from 'joi';
import axios from 'axios';

export default function Register() {

  let [user,setUser] = useState({
    first_name:'',
    last_name:'',
    age:'',
    email:'',
    passowrd:'',


  });

  let [errorList,setErrorList] = useState([]);

  function submitFormData(e){
    e.preventDefault();
    axios.post("http://http://localhost:3000/api/v1/users")
    let validateResult = validateForm();
    if(validateResult.error){
      setErrorList(validateResult.error.details);
    }
  }

  function grtFormValue(e){
    let myUser = {...user};
    myUser[e.target.name]=e.target.value;
    setUser(myUser);
    
  }

  function validateForm(){
    const schema = Joi.object({
      first_name:Joi.string().required().min(3).max(15),
      last_name:Joi.string().required().min(3).max(15),
      age:Joi.number().required().min(18).max(80),
      email:Joi.string().required().email({  tlds: { allow: ['com'] } }),
      passowrd:Joi.string().required().pattern(new RegExp('^[0-9]{10}$'))
      
    });
    return schema.validate(user,{abortEarly:false});
  }

  return (
    <div>
  <h1 className="my-5">Register</h1>

    {errorList.map( (error,index )=> <div key={index} className="alert alert-danger" >
      {error.message}
    </div>)}

  <form onSubmit={submitFormData}>
    <div className="mb-3">
      <label htmlFor="first_name" className="form-label">First Name</label>
      <input onChange={grtFormValue} type="text" name="first_name" className="form-control" id="first_name" />
    </div>

    <div className="mb-3">
      <label htmlFor="last_name" className="form-label">Last Name</label>
      <input onChange={grtFormValue} type="text" name="last_name" className="form-control" id="last_name" />
    </div>

    <div className="mb-3">
      <label htmlFor="age" className="form-label">Age</label>
      <input onChange={grtFormValue} type="text" name="age" className="form-control" id="age" />
    </div>

    <div className="mb-3">
      <label htmlFor="email" className="form-label">Email</label>
      <input onChange={grtFormValue} type="text" name="email" className="form-control" id="email" />
    </div>

    <div className="mb-3">
      <label htmlFor="passowrd" className="form-label">Passowrd</label>
      <input onChange={grtFormValue} type="passowrd" name="passowrd" className="form-control" id="passowrd" />
    </div>

    <button className='btn btn-info'>Register</button>
  </form>

    </div>
  )
}
